mapper.insert:

insert into
user (id, password, name, region, sub_date)
values( #{id}, #{password}, #{name}, #{region}, now());

mapper.select:

select id, name, role
from user 
where id = #{id } and password = #{password }


select id, name, region, sub_date, role 
from user 
where id = #{id }

select id, name, region
from user
where id = #{id }
